package com.google.common.util.concurrent; public class AtomicDouble extends Number { private double value; public AtomicDouble(double v) { value = v; } public double get() { return value; } public double addAndGet(double v) { value += v; return value; } public double updateAndGet(java.util.function.DoubleUnaryOperator op) { value = op.applyAsDouble(value); return value; } @Override public int intValue() { return (int)value; } @Override public long longValue() { return (long)value; } @Override public float floatValue() { return (float)value; } @Override public double doubleValue() { return value; } }
